package com.icefox.bean;

import java.io.FileOutputStream;
import java.io.OutputStream;

import sun.misc.BASE64Decoder;

public class StringToPicture {
	
	public static void main(String[] args) {
		//身份证图片
		String pictureStr = "";
		try {
			byte[] b =  (new BASE64Decoder()).decodeBuffer(pictureStr);
			for (int i = 0; i < b.length; ++i) {
				if (b[i] < 0) {// 调整异常数据
					b[i] += 256;
				}
			}
			OutputStream out = new FileOutputStream("D:\\picture.png");
			out.write(b);
			out.flush();
			out.close();
		} catch (Exception e) {
			e.printStackTrace();
		}
	
	}
	
}
